( cat *.txt | egrep -v '^ *$' | egrep -v '^#'  ;  ruby -rubygems -e 'require "yaml"; Dir["*.yaml"].sort.each{|f| next if f =~ /alternative_dictionaries_multi/ ; yy = YAML.load(File.open(f)) ; payload = yy.first["dataset"]["payload"] rescue {} ; payload.map{|wl| wl["word_list"].map{|w| puts w["word"] } } }'  ) | ruby -E utf-8 -ne 'puts $_.gsub(%r{:[^/].*}, "").strip.downcase' | sort  | uniq -c | sort -rn | ruby -E utf-8 -ne 'puts $_.strip.split(/\s+/,2).join("\t") ' > ../combined.tsv 

